package pacote_fundamentos;

public class Constantes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Constantes devem sempre ser escritas em letra maiúsculas
		// Se for um nome composto separar cada nome com underline
		final double PI=3.14159265358979323846264338327950288 ;
		// PI=3.12;
		System.out.println("Este é valor de PI: "+PI);
		// O conteúdo da variável do tipo char deve sempre ser carregado com aspas simples.
		 final char SEXO_MASCULINO='M';
		 final char SEXO_FEMININO='F';
		 System.out.println("Este é valor de SEXO_MASCULINO: "+SEXO_MASCULINO);
		 System.out.println("Este é valor de SEXO_FEMININO: "+SEXO_FEMININO);
		 final double FUNDO_GARANTIA=0.08;
		 
		 System.out.println("Este é valor de FUNDO_GARANTIA: "+FUNDO_GARANTIA);
	}

}
