package pacote_logica_programacao;

public class LPJ001 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int valor = 1;
		
		System.out.println(valor);
		System.out.println("");
		
		valor += 2; 
		
		System.out.println(valor);
		System.out.println("");
		valor -= 1;
		
		System.out.println(valor);
		System.out.println("");
		valor *= 6;
		
		System.out.println(valor);
		System.out.println("");
		valor /= 3;
		
		System.out.println(valor);
		System.out.println("");
		valor %= 3;
		
		System.out.println(valor);
		System.out.println("");
		valor ++; 
		
		System.out.println(valor);
		System.out.println("");
		valor --;
		
		System.out.println(valor);
		System.out.println("");
	}

}
