package pacote_logica_programacao;

public class LPJ002 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a = 7;
		System . out. println (a);
		a += 3;
		System . out. println (" ");
		System . out. println (a);
		a *= 2;
		System . out. println (" ");
		System . out. println (a);
		a ++;
		System . out. println (" ");
		System . out. println (a);
		a -= 1.82;
		System . out. println (" ");
		System . out. println (a);
		}

	}


