package pacote_logica_programacao;

public class LPJ003 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int valor = 2;
		@SuppressWarnings("unused")
		boolean b;
		b = ( valor == 2); // b = true
		b = ( valor != 2); // b = false
		b = ( valor < 2); // b = false
		b = ( valor <= 2); // b = true
		b = ( valor > 1); // b = true
		b = ( valor >= 1); // b = true
		
	}

}
